import React, { Component } from 'react'

class FirstCompnent extends Component {
    state = { 
        firstNum : '0',
        secondNum : ''
    } 
    numberMaker  = (num) =>{
        if(this.state.firstNum.length !== 0){
            this.setState({firstNum : this.state.firstNum + 1})
            console.log(this.state.a)
        } 
        else{
            this.setState({firstNum : num})
        }       
    }
    render() { 
        return (
            <div className='container p-0 m-0'>
                <header>
                    <div>Calculus</div>
                    <div></div>
                </header>
                <main className='container'>
                    <div className='row'>
                    <div className='col'>
                        <div className='container'>
                            <div className='row'><button className='btn col bg-dark text-light' onClick={()=>{
                                this.numberMaker('1')

                            }}>1</button> <button className='btn col bg-dark text-light'>2</button> <button className='btn col bg-dark text-light'>3</button></div>

                        </div>
                        <div className='container'>
                        <div className='row'><button className='btn col bg-dark text-light'>4</button> <button className='btn col bg-dark text-light'>5</button> <button className='btn col bg-dark text-light'>6</button></div>
                        </div>
                        <div className='container'>
                            <div className='row'><button className='btn col bg-dark text-light'>7</button> <button className='btn col bg-dark text-light'>8</button> <button className='btn col bg-dark text-light'>9</button></div>
                        </div>
                        <div className='container'>
                            <div className='row'><button className='btn col bg-dark text-light'>0</button> <button className='btn col bg-warning'> = </button></div></div>
                    </div>
                    <div className='col container'>
                        <div className='row'>
                    <button className='btn bg-warning'>+</button> <button className='btn bg-warning'>-</button> <button className='btn bg-warning'>x</button> <button className='btn bg-warning'>%</button>
                    </div>
                    </div>
                    </div>
                </main>
            </div>
        );
    }
}

export default FirstCompnent;
