import React, { Component } from 'react';

class Ehsan extends Component { 
    constructor(props){
        super(props);
    }
    state ={
            inputValue : ''
        }
    changabke = event =>{
        this.setState({inputValue : event.target.value})
    }
    render(){
        return (
            <div>
            <header className='bg-info'>
                <div className='text-light'>ToDo</div>
           </header>
           <div><input type="text" value={this.state.inputValue} onChange ={this.changabke()} /></div>
           </div>
        );

    }
}

export default Ehsan;
