import React, { Component } from 'react';

class Ehsan extends Component { 
    constructor(props){
        super(props);
        this.reset();
    }
    reset(){
        this.state ={
            inputValue : ''
        }
    }
    render(){
        return (
            <div>
            <header className='bg-info'>
                <div className='text-bg-light'>ToDo</div>
            </header>
            <main>
                <div>
                    <form action="">
                        <input type="text" value={this.reset.state.inputValue} onChange={evt =>{this.setState({inputValue : evt})}}  />
                        <input type="text" />
                    </form>
                </div>
                <div>

                </div>
            </main>
            </div>
        );

    }
}

export default Ehsan;
