import React, { Component } from 'react';

class Ehsan extends Component { 
    state = {
        count : 0
        };
    constructor(props){
        super(props);
        this.firstfunction  = this.firstfunction.bind(this);
    }
    firstfunction() {
        this.setState({count : this.state.count + 1});

    }
    maker = () =>{
        console.log(this.state.count);
        if(this.state.count !== 0){
            return (
                <p>hello world</p>
            )
        }
    }
    render(){
        return (
            <header className='bg-info'>
                <button onClick={this.firstfunction}>ToDo
                </button>
                {this.maker}
            </header>
        );

    }
}

export default Ehsan;
